mapper.insert:
    INSERT INTO USER_INFO
		(USER_SEQ
		, USER_ID
		, USER_PW
		, USER_NAME
		, USER_BIRTH
		
		, USER_TEL_NO
		, AUTH
		, `LEVEL`
		, JOIN_DATE
		, UPDATE_DATE)
	VALUES((SELECT  aa.USER_SEQ 
			  FROM( select IFNULL(max(USER_SEQ)+1 , CONCAT(CURDATE()+0,'001')) as USER_SEQ
				      from USER_INFO where SUBSTR(USER_SEQ,1,8) =  CURDATE()+0
			) AS aa)
		 , #{USER_ID}
		 , #{USER_PW}
		 , #{USER_NAME}
		 , #{USER_BIRTH}
		
		 , #{USER_TEL_NO}
		 , #{AUTH}
		 , #{LEVEL}
		 , NOW()
		 , CURRENT_TIMESTAMP);
