mapper.select:
  	  SELECT COUNT(*)
		FROM USER_INFO
		WHERE 1=1
		AND USER_ID = #{email}
		AND USER_PW  =#{password}

mapper.insert:
    INSERT INTO joy0109.USER_INFO (
		USER_SEQ
	  , USER_NAME
	  , USER_EMP_NUM
	  , USER_BIRTH
	  , USER_ID
	  
	  , USER_PW
	  , USER_TEL1
	  , USER_TEL2
	  , USER_DEP_NUM
	  , USER_NICK
	  
	  , AUTH
	  , `LEVEL`
	  , JOIN_DATE
	  , UPDATE_DATE)
							
VALUES(	(SELECT  aa.USER_SEQ 
		  FROM( select IFNULL(max(USER_SEQ)+1 , CONCAT(CURDATE()+0,'001')) as USER_SEQ
				  from USER_INFO where SUBSTR(USER_SEQ,1,8) =  CURDATE()+0
		) AS aa)
	,#{USER_NAME}
	,#{USER_EMP_NUM}
	,#{USER_BIRTH}
	,#{USER_ID}

	,#{USER_PW}
	,#{USER_TEL1}
	,#{USER_TEL2}
	,#{USER_DEP_NUM}
	,#{USER_NICK}

	,#{AUTH}
	,#{LEVEL}
	, NOW()
	, CURRENT_TIMESTAMP);
